import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardActions,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Snackbar,
  Alert,
  Box,
  CircularProgress,
  Fab,
  Zoom,
  Paper,
  styled,
  IconButton,
  Pagination
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import CategoryIcon from '../components/CategoryIcon';
import InventoryService from '../services/InventoryService';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const AnimatedFab = styled(Fab)(({ theme }) => ({
  position: 'fixed',
  bottom: theme.spacing(4),
  right: theme.spacing(4),
  transition: 'width 0.3s ease-in-out',
  minWidth: '56px',
  width: '56px',
  height: '56px',
  borderRadius: '28px',
  backgroundColor: theme.palette.primary.main,
  textTransform: 'none',
  '&:hover': {
    backgroundColor: theme.palette.primary.main,
  },
  '& .MuiSvgIcon-root': {
    transition: 'transform 0.3s ease-in-out',
    marginRight: 0,
    width: '24px',
    height: '24px',
  },
  '& .button-text': {
    width: 0,
    opacity: 0,
    transition: 'opacity 0.2s ease-in-out, width 0s 0.2s',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    display: 'inline-block',
    fontSize: '0.875rem',
    fontWeight: 500,
    visibility: 'hidden',
    textTransform: 'none',
  },
  '&:hover': {
    width: 'auto',
    paddingRight: theme.spacing(4),
    paddingLeft: theme.spacing(3),
    backgroundColor: theme.palette.primary.main,
    '& .MuiSvgIcon-root': {
      transform: 'rotate(90deg)',
      marginRight: theme.spacing(1),
    },
    '& .button-text': {
      width: 'auto',
      opacity: 1,
      visibility: 'visible',
      marginLeft: theme.spacing(0.5),
      transition: 'opacity 0.2s ease-in-out, width 0s',
    },
  },
  '&:active': {
    backgroundColor: theme.palette.primary.main,
    boxShadow: theme.shadows[6],
  },
}));

const InventoryContainer = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  borderRadius: theme.spacing(2),
  backgroundColor: 'rgba(255, 255, 255, 0.8)',
  backdropFilter: 'blur(10px)',
  boxShadow: 'rgba(0, 0, 0, 0.05) 0px 6px 24px 0px, rgba(0, 0, 0, 0.08) 0px 0px 0px 1px',
  minHeight: '70vh',
  display: 'flex',
  flexDirection: 'column',
}));

const EmptyState = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  height: '100%',
  minHeight: '400px',
  color: theme.palette.text.secondary,
  '& svg': {
    fontSize: '64px',
    marginBottom: theme.spacing(2),
    opacity: 0.5,
  },
}));

function InventoryListScreen() {
  const [inventory, setInventory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const data = await InventoryService.getAllItems();
      setInventory(data);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = (itemId) => {
    setItemToDelete(itemId);
    setDeleteDialogOpen(true);
  };

  const confirmDelete = async () => {
    try {
      await InventoryService.deleteItem(itemToDelete);
      setInventory(prev => prev.filter(item => item._id !== itemToDelete));
      setDeleteDialogOpen(false);
      setItemToDelete(null);
    } catch (error) {
      setError(error.message);
    }
  };

  // Group items by location
  const groupedItems = inventory.reduce((acc, item) => {
    const location = item.location || 'unassigned';
    if (!acc[location]) {
      acc[location] = [];
    }
    acc[location].push(item);
    return acc;
  }, {});

  const onDragEnd = async (result) => {
    if (!result.destination) return;

    const { source, destination } = result;
    const sourceLocation = source.droppableId;
    const destLocation = destination.droppableId;

    // If dropped in the same location, just reorder
    if (sourceLocation === destLocation) {
      const items = [...inventory];
      const sourceItems = sourceLocation === 'unassigned' 
        ? items.filter(item => !item.location)
        : items.filter(item => item.location === sourceLocation);
      
      const [movedItem] = sourceItems.splice(source.index, 1);
      sourceItems.splice(destination.index, 0, movedItem);
      
      // Update state
      setInventory(prev => {
        const filtered = prev.filter(item => 
          sourceLocation === 'unassigned' 
            ? item.location 
            : item.location !== sourceLocation
        );
        return [...filtered, ...sourceItems];
      });
      return;
    }

    // Moving between locations
    const items = [...inventory];
    const sourceItems = sourceLocation === 'unassigned' 
      ? items.filter(item => !item.location)
      : items.filter(item => item.location === sourceLocation);
    
    const [movedItem] = sourceItems.splice(source.index, 1);
    
    // Update the item's location
    const updatedItem = {
      ...movedItem,
      location: destLocation === 'unassigned' ? '' : destLocation
    };

    try {
      // Update in the database
      await InventoryService.updateItem(updatedItem._id, updatedItem);
      
      // Update local state
      setInventory(prev => 
        prev.map(item => item._id === updatedItem._id ? updatedItem : item)
      );
    } catch (error) {
      setError(error.message);
      // Revert the move if the update failed
      sourceItems.splice(source.index, 0, movedItem);
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 8 }}>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h5" component="h2">
          Inventory Items
        </Typography>
      </Box>

      <DragDropContext onDragEnd={onDragEnd}>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
          {Object.entries(groupedItems).map(([location, items]) => (
            <Paper
              key={location}
              elevation={2}
              sx={{
                p: 2,
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                backdropFilter: 'blur(10px)',
              }}
            >
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                <Typography variant="h6">
                  {location}
                </Typography>
              </Box>
              <Droppable droppableId={location} direction="horizontal">
                {(provided, snapshot) => (
                  <Box
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    sx={{
                      display: 'flex',
                      gap: 2,
                      minHeight: '200px',
                      overflowX: 'auto',
                      pb: 1,
                      backgroundColor: snapshot.isDraggingOver ? 'rgba(0, 0, 0, 0.05)' : 'transparent',
                      borderRadius: 1,
                      transition: 'background-color 0.2s ease',
                      '&::-webkit-scrollbar': {
                        height: '8px',
                      },
                      '&::-webkit-scrollbar-track': {
                        background: '#f1f1f1',
                        borderRadius: '4px',
                      },
                      '&::-webkit-scrollbar-thumb': {
                        background: '#888',
                        borderRadius: '4px',
                        '&:hover': {
                          background: '#555',
                        },
                      },
                    }}
                  >
                    {items.map((item, index) => (
                      <Draggable
                        key={item._id}
                        draggableId={item._id}
                        index={index}
                      >
                        {(provided, snapshot) => (
                          <Card
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            style={{
                              ...provided.draggableProps.style,
                              transform: snapshot.isDragging
                                ? `${provided.draggableProps.style?.transform} scale(1.05)`
                                : provided.draggableProps.style?.transform
                            }}
                            sx={{
                              cursor: 'grab',
                              '&:active': {
                                cursor: 'grabbing',
                              },
                              backgroundColor: 'white',
                              borderRadius: '16px',
                              minWidth: '300px',
                              flexShrink: 0,
                              boxShadow: snapshot.isDragging ? 3 : 1,
                              transition: 'all 0.2s ease'
                            }}
                          >
                            <CategoryIcon 
                              category={item.category.name}
                              isCustom={!item.category.isDefault}
                            />
                            <CardContent sx={{ 
                              flexGrow: 1, 
                              pt: 0,
                              pb: 1,
                              px: 2,
                              '&:last-child': { pb: 1 }
                            }}>
                              <Typography variant="h6" sx={{ 
                                fontSize: '1.1rem',
                                fontWeight: 500,
                                mb: 1
                              }}>
                                {item.name}
                              </Typography>
                              <Box sx={{ 
                                display: 'flex', 
                                flexDirection: 'column',
                                gap: 0.5,
                                color: 'text.secondary',
                                fontSize: '0.875rem'
                              }}>
                                <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                                  Quantity: {item.quantity}
                                </Typography>
                                <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                                  Price: ${item.price}
                                </Typography>
                                <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                                  Category: {item.category.name}
                                </Typography>
                              </Box>
                            </CardContent>
                            <CardActions sx={{ 
                              justifyContent: 'flex-end', 
                              p: 1,
                              pt: 0,
                              borderTop: '1px solid',
                              borderColor: 'divider',
                              minHeight: '36px'
                            }}>
                              <IconButton
                                size="small"
                                onClick={(e) => {
                                  e.stopPropagation();
                                  handleDelete(item._id);
                                }}
                                sx={{ color: 'text.secondary' }}
                              >
                                <DeleteIcon fontSize="small" />
                              </IconButton>
                            </CardActions>
                          </Card>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </Box>
                )}
              </Droppable>
            </Paper>
          ))}
        </Box>
      </DragDropContext>

      <Zoom in={true}>
        <AnimatedFab
          color="primary"
          onClick={() => navigate('/add-item')}
          aria-label="add new item"
          size="large"
        >
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center',
            justifyContent: 'center',
            height: '100%'
          }}>
            <AddIcon />
            <span className="button-text">Add new item</span>
          </Box>
        </AnimatedFab>
      </Zoom>

      <Dialog
        open={deleteDialogOpen}
        onClose={() => setDeleteDialogOpen(false)}
      >
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete this item? This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
          <Button onClick={confirmDelete} color="error" variant="contained">
            Delete
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar 
        open={!!error} 
        autoHideDuration={6000} 
        onClose={() => setError('')}
      >
        <Alert severity="error" onClose={() => setError('')}>
          {error}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default InventoryListScreen;
