import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  Grid,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Snackbar,
  Alert
} from '@mui/material';
import InventoryService from '../services/InventoryService';
import InventoryItem from '../models/InventoryItem';

class ItemManager {
  constructor(setItem, setError, navigate) {
    this.setItem = setItem;
    this.setError = setError;
    this.navigate = navigate;
  }

  async fetchItem(id) {
    try {
      const data = await InventoryService.getItem(id);
      this.setItem(new InventoryItem(data));
    } catch (error) {
      this.setError(error.message);
    }
  }

  async saveItem(item, id = null) {
    try {
      const errors = item.validate();
      if (errors.length > 0) {
        this.setError(errors.join(', '));
        return false;
      }

      if (id) {
        await InventoryService.updateItem(id, item.toJSON());
      } else {
        await InventoryService.createItem(item.toJSON());
      }
      this.navigate('/inventory');
      return true;
    } catch (error) {
      this.setError(error.message);
      return false;
    }
  }

  async deleteItem(id) {
    try {
      await InventoryService.deleteItem(id);
      this.navigate('/inventory');
      return true;
    } catch (error) {
      this.setError(error.message);
      return false;
    }
  }
}

function AddItemScreen() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [item, setItem] = useState(new InventoryItem());
  const [error, setError] = useState('');
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  
  const itemManager = new ItemManager(setItem, setError, navigate);

  useEffect(() => {
    if (id) {
      itemManager.fetchItem(id);
    }
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await itemManager.saveItem(item, id);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setItem(prev => new InventoryItem({ ...prev, [name]: value }));
  };

  const handleDelete = async () => {
    if (await itemManager.deleteItem(id)) {
      setDeleteDialogOpen(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        <Typography variant="h5" component="h2" gutterBottom>
          {id ? 'Edit Item' : 'Add New Item'}
        </Typography>
        <Box component="form" onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Name"
                name="name"
                value={item.name}
                onChange={handleChange}
                required
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Description"
                name="description"
                value={item.description}
                onChange={handleChange}
                multiline
                rows={4}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Quantity"
                name="quantity"
                type="number"
                value={item.quantity}
                onChange={handleChange}
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Price"
                name="price"
                type="number"
                value={item.price}
                onChange={handleChange}
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Category"
                name="category"
                value={item.category}
                onChange={handleChange}
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Location"
                name="location"
                value={item.location}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                {/* Delete button (only show when editing) */}
                {id && (
                  <Button
                    variant="contained"
                    color="error"
                    onClick={() => setDeleteDialogOpen(true)}
                  >
                    Delete Item
                  </Button>
                )}
                <Box sx={{ display: 'flex', gap: 2 }}>
                  <Button
                    variant="outlined"
                    onClick={() => navigate('/inventory')}
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                  >
                    {id ? 'Update' : 'Add'} Item
                  </Button>
                </Box>
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Paper>

      <Dialog
        open={deleteDialogOpen}
        onClose={() => setDeleteDialogOpen(false)}
      >
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete this item? This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleDelete} color="error" variant="contained">
            Delete
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar 
        open={!!error} 
        autoHideDuration={6000} 
        onClose={() => setError('')}
      >
        <Alert severity="error" onClose={() => setError('')}>
          {error}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default AddItemScreen;
