import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardActions,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Snackbar,
  Alert,
  Box,
  CircularProgress,
  Fab,
  Zoom,
  Paper,
  styled,
  IconButton
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import CategoryIcon from '../components/CategoryIcon';
import InventoryService from '../services/InventoryService';
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragOverlay,
} from '@dnd-kit/core';
import {
  arrayMove,
  SortableContext,
  sortableKeyboardCoordinates,
  horizontalListSortingStrategy,
  useSortable,
} from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';

const AnimatedFab = styled(Fab)(({ theme }) => ({
  position: 'fixed',
  bottom: theme.spacing(4),
  right: theme.spacing(4),
  transition: 'width 0.3s ease-in-out',
  minWidth: '56px',
  width: '56px',
  height: '56px',
  borderRadius: '28px',
  backgroundColor: theme.palette.primary.main,
  textTransform: 'none',
  '&:hover': {
    backgroundColor: theme.palette.primary.main,
  },
  '& .MuiSvgIcon-root': {
    transition: 'transform 0.3s ease-in-out',
    marginRight: 0,
    width: '24px',
    height: '24px',
  },
  '& .button-text': {
    width: 0,
    opacity: 0,
    transition: 'opacity 0.2s ease-in-out, width 0s 0.2s',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    display: 'inline-block',
    fontSize: '0.875rem',
    fontWeight: 500,
    visibility: 'hidden',
    textTransform: 'none',
  },
  '&:hover': {
    width: 'auto',
    paddingRight: theme.spacing(4),
    paddingLeft: theme.spacing(3),
    backgroundColor: theme.palette.primary.main,
    '& .MuiSvgIcon-root': {
      transform: 'rotate(90deg)',
      marginRight: theme.spacing(1),
    },
    '& .button-text': {
      width: 'auto',
      opacity: 1,
      visibility: 'visible',
      marginLeft: theme.spacing(0.5),
      transition: 'opacity 0.2s ease-in-out, width 0s',
    },
  },
  '&:active': {
    backgroundColor: theme.palette.primary.main,
    boxShadow: theme.shadows[6],
  },
}));

const InventoryContainer = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  borderRadius: theme.spacing(2),
  backgroundColor: 'rgba(255, 255, 255, 0.8)',
  backdropFilter: 'blur(10px)',
  boxShadow: 'rgba(0, 0, 0, 0.05) 0px 6px 24px 0px, rgba(0, 0, 0, 0.08) 0px 0px 0px 1px',
  minHeight: '70vh',
  display: 'flex',
  flexDirection: 'column',
}));

const EmptyState = styled(Box)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  height: '100%',
  minHeight: '400px',
  color: theme.palette.text.secondary,
  '& svg': {
    fontSize: '64px',
    marginBottom: theme.spacing(2),
    opacity: 0.5,
  },
}));

function SortableItem({ item, handleDelete }) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: item._id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
  };

  return (
    <Card
      ref={setNodeRef}
      style={style}
      {...attributes}
      {...listeners}
      sx={{
        cursor: 'grab',
        '&:active': {
          cursor: 'grabbing',
        },
        backgroundColor: 'white',
        borderRadius: '16px',
        minWidth: '300px',
        flexShrink: 0,
        boxShadow: isDragging ? 3 : 1,
        transition: 'all 0.2s ease'
      }}
    >
      <CategoryIcon 
        category={item.category.name}
        isCustom={!item.category.isDefault}
      />
      <CardContent sx={{ 
        flexGrow: 1, 
        pt: 0,
        pb: 1,
        px: 2,
        '&:last-child': { pb: 1 }
      }}>
        <Typography variant="h6" sx={{ 
          fontSize: '1.1rem',
          fontWeight: 500,
          mb: 1
        }}>
          {item.name}
        </Typography>
        <Box sx={{ 
          display: 'flex', 
          flexDirection: 'column',
          gap: 0.5,
          color: 'text.secondary',
          fontSize: '0.875rem'
        }}>
          <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
            Quantity: {item.quantity}
          </Typography>
          <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
            Price: ${item.price}
          </Typography>
          <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
            Category: {item.category.name}
          </Typography>
        </Box>
      </CardContent>
      <CardActions sx={{ 
        justifyContent: 'flex-end', 
        p: 1,
        pt: 0,
        borderTop: '1px solid',
        borderColor: 'divider',
        minHeight: '36px'
      }}>
        <IconButton
          size="small"
          onClick={(e) => {
            e.stopPropagation();
            handleDelete(item._id);
          }}
          sx={{ color: 'text.secondary' }}
        >
          <DeleteIcon fontSize="small" />
        </IconButton>
      </CardActions>
    </Card>
  );
}

function InventoryListScreen() {
  const [inventory, setInventory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const [activeId, setActiveId] = useState(null);
  const navigate = useNavigate();

  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const data = await InventoryService.getAllItems();
      setInventory(data);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = (itemId) => {
    setItemToDelete(itemId);
    setDeleteDialogOpen(true);
  };

  const confirmDelete = async () => {
    try {
      await InventoryService.deleteItem(itemToDelete);
      setInventory(prev => prev.filter(item => item._id !== itemToDelete));
      setDeleteDialogOpen(false);
      setItemToDelete(null);
    } catch (error) {
      setError(error.message);
    }
  };

  // Group items by location
  const groupedItems = inventory.reduce((acc, item) => {
    const location = item.location || 'unassigned';
    if (!acc[location]) {
      acc[location] = [];
    }
    acc[location].push(item);
    return acc;
  }, {});

  // Sort locations alphabetically, with 'unassigned' at the bottom
  const sortedLocations = Object.keys(groupedItems).sort((a, b) => {
    if (a === 'unassigned') return 1;
    if (b === 'unassigned') return -1;
    return a.localeCompare(b);
  });

  const handleDragStart = (event) => {
    setActiveId(event.active.id);
  };

  const handleDragEnd = async (event) => {
    const { active, over } = event;
    
    if (!over) return;

    if (active.id !== over.id) {
      const activeItem = inventory.find(item => item._id === active.id);
      const overItem = inventory.find(item => item._id === over.id);
      
      if (activeItem && overItem) {
        const updatedItem = {
          ...activeItem,
          location: overItem.location
        };

        try {
          await InventoryService.updateItem(updatedItem._id, updatedItem);
          setInventory(prev => 
            prev.map(item => item._id === updatedItem._id ? updatedItem : item)
          );
        } catch (error) {
          setError(error.message);
        }
      }
    }

    setActiveId(null);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 8 }}>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h5" component="h2">
          Inventory Items
        </Typography>
      </Box>

      <DndContext
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd}
      >
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
          {sortedLocations.map((location) => (
            <Paper
              key={location}
              elevation={2}
              sx={{
                p: 2,
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                backdropFilter: 'blur(10px)',
              }}
            >
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                <Typography variant="h6">
                  {location === 'unassigned' ? 'Unassigned' : location}
                </Typography>
              </Box>
              <Box
                sx={{
                  display: 'flex',
                  gap: 2,
                  minHeight: '200px',
                  overflowX: 'auto',
                  pb: 1,
                  borderRadius: 1,
                  '&::-webkit-scrollbar': {
                    height: '8px',
                  },
                  '&::-webkit-scrollbar-track': {
                    background: '#f1f1f1',
                    borderRadius: '4px',
                  },
                  '&::-webkit-scrollbar-thumb': {
                    background: '#888',
                    borderRadius: '4px',
                    '&:hover': {
                      background: '#555',
                    },
                  },
                }}
              >
                <SortableContext
                  items={groupedItems[location].map(item => item._id)}
                  strategy={horizontalListSortingStrategy}
                >
                  {groupedItems[location].map((item) => (
                    <SortableItem
                      key={item._id}
                      item={item}
                      handleDelete={handleDelete}
                    />
                  ))}
                </SortableContext>
              </Box>
            </Paper>
          ))}
        </Box>
        <DragOverlay>
          {activeId ? (
            <Card
              sx={{
                backgroundColor: 'white',
                borderRadius: '16px',
                minWidth: '300px',
                flexShrink: 0,
                boxShadow: 3,
                opacity: 0.8,
                transform: 'scale(1.05)',
                transition: 'all 0.2s ease',
              }}
            >
              {(() => {
                const activeItem = inventory.find(item => item._id === activeId);
                return activeItem ? (
                  <>
                    <CategoryIcon 
                      category={activeItem.category.name}
                      isCustom={!activeItem.category.isDefault}
                    />
                    <CardContent sx={{ 
                      flexGrow: 1, 
                      pt: 0,
                      pb: 1,
                      px: 2,
                      '&:last-child': { pb: 1 }
                    }}>
                      <Typography variant="h6" sx={{ 
                        fontSize: '1.1rem',
                        fontWeight: 500,
                        mb: 1
                      }}>
                        {activeItem.name}
                      </Typography>
                      <Box sx={{ 
                        display: 'flex', 
                        flexDirection: 'column',
                        gap: 0.5,
                        color: 'text.secondary',
                        fontSize: '0.875rem'
                      }}>
                        <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                          Quantity: {activeItem.quantity}
                        </Typography>
                        <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                          Price: ${activeItem.price}
                        </Typography>
                        <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                          Category: {activeItem.category.name}
                        </Typography>
                      </Box>
                    </CardContent>
                    <CardActions sx={{ 
                      justifyContent: 'flex-end', 
                      p: 1,
                      pt: 0,
                      borderTop: '1px solid',
                      borderColor: 'divider',
                      minHeight: '36px'
                    }}>
                      <IconButton
                        size="small"
                        sx={{ color: 'text.secondary' }}
                      >
                        <DeleteIcon fontSize="small" />
                      </IconButton>
                    </CardActions>
                  </>
                ) : null;
              })()}
            </Card>
          ) : null}
        </DragOverlay>
      </DndContext>

      <Zoom in={true}>
        <AnimatedFab
          color="primary"
          onClick={() => navigate('/add-item')}
          aria-label="add new item"
          size="large"
        >
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center',
            justifyContent: 'center',
            height: '100%'
          }}>
            <AddIcon />
            <span className="button-text">Add new item</span>
          </Box>
        </AnimatedFab>
      </Zoom>

      <Dialog
        open={deleteDialogOpen}
        onClose={() => setDeleteDialogOpen(false)}
      >
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete this item? This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
          <Button onClick={confirmDelete} color="error" variant="contained">
            Delete
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar 
        open={!!error} 
        autoHideDuration={6000} 
        onClose={() => setError('')}
      >
        <Alert severity="error" onClose={() => setError('')}>
          {error}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default InventoryListScreen;
